"""Subclass of Working, which is generated by wxFormBuilder."""

import sys, os


from beatle.app import Working as base


# Implementing Working
class WorkingDialog(base):
    """Dialog for showing wait"""
    def __init__(self, parent):
        """Initialize dialog"""
        super(WorkingDialog, self).__init__(parent)
        path = os.path.split(sys.argv[0])[0]
        self.m_animCtrl.LoadFile(os.path.join(path, "app/res/wait.gif"))
        self.m_animCtrl.Play()

